import React, { useEffect } from "react";
import { useState } from "react";
import { nanoid } from "nanoid";
import Left from "./components/Left";
import Right from "./components/Right";
import Modal from "./components/Modal";

const App = () => {
  const [notes, setNotes] = useState([
    {
      textTitle: "CSE 316",
      date: "3/15/2022, 14:42:36 PM",
      text: "CSE 316",
      // id: nanoid()
    },
    {
      textTitle: "Another wrapping line example! This is another text",
      date: "3/15/2022, 14:42:30 PM",
      text: "Another wrapping line example! This is another text",
      // id: nanoid()
    },
  ]);
  const [windowWidth, setWindowWidth] = useState(0);

  const [seletedId, setSeletedId] = useState(-1);

  //addNewNote
  const addNewNote = (text) => {
    const newNote = {
      textTitle: "New Note",
      date: getTimeAndDate(),
      text: "",
    };

    const newNoteList = [newNote, ...notes];

    setNotes(newNoteList);

    setSeletedId(0);
  };

  //getTimeAndDate
  const getTimeAndDate = () => {
    let today = new Date();

    let year = today.getFullYear().toString();
    let month = (today.getMonth() + 1).toString();
    let date = today.getDate().toString();

    let hours = today.getHours().toString() % 12 || 12;
    let minutes = today.getMinutes().toString();
    let seconds = today.getSeconds().toString();

    let ampm = hours < 12 ? "PM" : "AM";

    return (
      month +
      "/" +
      date +
      "/" +
      year +
      ", " +
      hours +
      ":" +
      minutes +
      ":" +
      seconds +
      " " +
      ampm
    );
  };

  //deleteNote
  const deleteNote = () => {
    if (seletedId === notes.length - 1) {
      setSeletedId(notes.length - 2);
    }

    //  [ ...notes.filter((eachNote, idx)=>idx!=seletedId)]
    setNotes([...notes.filter((eachNote, idx) => idx != seletedId)]);
  };

  //saving notes to local storage
  useEffect(() => {
    const savedNotes = JSON.parse(localStorage.getItem("notes-data"));

    if (savedNotes) {
      setNotes(savedNotes);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("notes-data", JSON.stringify(notes));
  }, [notes]);

  useEffect(() => {
    window.addEventListener("resize", () => {});
    return () => {};
  }, []);

  return (
    <div className="whole-note" id="whole-note">
      <Left
        notes={notes}
        addNewNote={addNewNote}
        seletedId={seletedId}
        setSeletedId={setSeletedId}
      />
      <Right
        deleteNote={deleteNote}
        notes={notes}
        setNotes={setNotes}
        seletedId={seletedId}
        getTimeAndDate={getTimeAndDate}
      />
      <Modal />
    </div>
  );
};

export default App;
