import React, { Component, useState } from "react";

const Tag = () => {
  const [tags, setTags] = useState(["CSE 316", "Example tag"]);
  const addTags = (event) => {
    if (event.key === "Enter") {
      setTags([...tags, event.target.value]);
      event.target.value = "";
    }
  };
  const deleteTags = (idxToDelete) => {
    setTags(tags.filter());
  };
  console.log(tags);
  return (
    <div className="wholeTagBox">
      <div className="wholeTagBox2">
        {tags.map((tag, idx) => (
          <div className="a-tag" key={idx}>
            <div className="a-tagText">{tag}</div>
            <button>
              <div
                className="material-icons closeTag"
                onClick={() => deleteTags(idx)}
              >
                close
              </div>
            </button>
          </div>
        ))}
      </div>
      <input
        className="tag-input"
        placeholder="Enter a tag"
        onKeyUp={addTags}
      ></input>
    </div>
  );
};

export default Tag;
