const Right = ({deleteNote, notes, setNotes, seletedId}) =>{
const saveNote = (newText) =>
{
    setNotes(notes.map((note)=>(
        note.id ===seletedId ? {...note, text:newText} : note)
    ))
}
    return(
        <div className="right">
            <div className="top-icons">
                <div className="only-view-small-screen">
                <div className="material-icons clickable">arrow_back</div>
                </div>
                <div className="material-icons clickable">notification_add</div>
                <div className="material-icons clickable">person_add_alt</div>
                <div className="material-icons clickable" onClick={deleteNote}>delete_outline</div>
            </div>

            <div className="div-for-text">
                <p id="texts">
                    <textarea className="text-input" id="text-input" value={notes[seletedId].text||""} onKeyUp={e => saveNote(e.currentTarget.value)} disabled={seletedId<0}>



                    </textarea>
                </p>
            </div>
        </div>

    );
};
export default Right;
