const Right = ({ deleteNote, notes, setNotes, seletedId, getTimeAndDate }) => {
  const saveNote = (newText) => {
    console.log(newText);
    if (notes[seletedId].text == "") {
      notes[seletedId].textTitle = "New Note";
    } else {
      setNotes(() => [
        ...notes.map((note, idx) =>
          idx === seletedId ? { ...note, text: newText } : note
        ),
      ]);

      notes[seletedId].textTitle = newText;
    }
    notes[seletedId].date = getTimeAndDate();

    setNotes([
      ...notes.map((note, idx) =>
        idx === seletedId ? { ...note, text: newText } : note
      ),
    ]);
    console.log(notes[seletedId]);
  };
  return (
    <div className="right">
      <div className="top-icons">
        <div className="only-view-small-screen">
          <div className="material-icons clickable">arrow_back</div>
        </div>
        <div className="material-icons clickable">notification_add</div>
        <div className="material-icons clickable">person_add_alt</div>
        <div className="material-icons clickable" onClick={deleteNote}>
          delete_outline
        </div>
      </div>

      <div className="div-for-text">
        <p id="texts">
          <textarea
            className="text-input"
            id="text-input"
            value={notes[seletedId]?.text || ""}
            onChange={(e) => {
              console.log(e.currentTarget.value);
              saveNote(e.currentTarget.value);
            }}
            disabled={seletedId < 0}
          ></textarea>
        </p>
      </div>
    </div>
  );
};
export default Right;
