const Right = ({
  deleteNote,
  notes,
  setNotes,
  seletedId,
  getTimeAndDate,
  setSeletedId,
}) => {
  const saveNote = (newText) => {
    console.log(newText);
    setNotes(() => {
      return [
        ...notes.map((note, idx) =>
          idx != seletedId
            ? note
            : newText == ""
            ? { ...note, text: "", textTitle: "New Note" }
            : { ...note, text: newText, textTitle: newText }
        ),
      ];
    });

    notes[seletedId].date = getTimeAndDate();
  };

  const visibleSidebar = () => {
    if (window.innerWidth <= 500) {
      document.getElementById("left").style.display = "block";
      document.getElementById("right").style.display = "none";
      document.getElementById("left").style.width = "100%";
    }
  };

  return (
    <div className="right" id="right">
      <div className="top-icons">
        <div className="only-view-small-screen">
          <div className="material-icons clickable" onClick={visibleSidebar}>
            arrow_back
          </div>
        </div>
        <div className="material-icons clickable">notification_add</div>
        <div className="material-icons clickable">person_add_alt</div>
        <div className="material-icons clickable" onClick={deleteNote}>
          delete_outline
        </div>
      </div>

      <div className="div-for-text">
        <p id="texts">
          <textarea
            className="text-input"
            id="text-input"
            value={notes[seletedId]?.text || ""}
            onChange={(e) => {
              console.log(e.currentTarget.value);
              saveNote(e.currentTarget.value);
            }}
            disabled={seletedId < 0}
          ></textarea>
        </p>
      </div>
    </div>
  );
};
export default Right;
