import { useState } from "react";
import Note from "./Note";
import Modal from "./Modal";
const Left = ({
  notes,
  addNewNote,
  seletedId,
  setSeletedId,
  windowWidth,
  setVisibleSidebar,
}) => {
  const openModal = () => {
    document.getElementById("modal-background").style.display = "block";
  };

  const orderedDate = notes.sort((a, b) => a.getTimeAndDate - b.getTimeAndDate);
  /*
      const orderedDate = notes.sort(
      (a, b) => a.getTimeAndDate - b.getTimeAndDate
    );
  */
  return (
    <div
      className="left"
      style={windowWidth <= 500 ? { width: "100%" } : {}}
      id="left"
    >
      <div className="main-title-box">
        <button className="profile-picture-button" onClick={openModal}></button>
        <button className="my-notes-title">My notes</button>
        <button className="add-new-note">
          <div className="material-icons" onClick={addNewNote}>
            note_add
          </div>
        </button>
      </div>

      <div className="contents">
        <div className="contents-in-search">
          <div className="search">
            <div className="material-icons search-icon">search</div>
          </div>
          <div>
            <input
              className="search-input"
              id="search"
              type={"text"}
              placeholder="Seach all notes"
            ></input>
          </div>
        </div>
        <div className="contents-in" id="contents-in">
          {notes.map((note, idx) => (
            <Note
              key={`note-${idx}`}
              textTitle={note.textTitle}
              date={note.date}
              text={note.text}
              onClick={() => {
                setSeletedId(idx);
                setVisibleSidebar(false);
                orderedDate();
              }}
              isSelected={seletedId === idx}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Left;
