import React from "react";
import { useState } from "react";
import {nanoid} from "nanoid";
import Left from "./components/Left";
import Right from "./components/Right";

const App = () => {
  const [notes, setNotes] = useState([
    {
      textTitle: "New Note", 
      date: '3/15/2022, 14:42:30 PM', 
      text: "", 
      id: nanoid
    },
    {
      textTitle: "New Note", 
      date: '3/15/2022, 14:42:35 PM', 
      text: "", 
      id: nanoid
    },
    {
      textTitle: "New Note", 
      date: '3/15/2022, 14:42:40 PM', 
      text: "", 
      id: nanoid
    },
  ]);

  //addNewNote
  const addNewNote = () =>{
    const newNote ={
        textTitle: "New Note", 
          date: getTimeAndDate(), 
          text: "", 
          id: nanoid
    };

    const newNoteList = [newNote, ...notes];
    setNotes(newNoteList);

    return(
    <div className="a-content">
        <div className="content-title">New Note</div>
        <div className="time-and-date"></div>
    </div>
    );
  }; 

  //getTimeAndDate
  const getTimeAndDate = () =>{
    let today = new Date();

    let year = today.getFullYear().toString(); 
    let month = (today.getMonth() + 1).toString();  
    let date = today.getDate().toString();  

    let hours = (today.getHours().toString()%12||12);
    let minutes = today.getMinutes().toString(); 
    let seconds = today.getSeconds().toString(); 

    let ampm = hours < 12 ? 'PM' : 'AM';

    return month+'/'+date+'/'+year+', '+hours+':'+minutes+':'+seconds+" "+ampm
 }



  return (
          <div className="whole-note" id="whole-note">
          <Left notes ={notes} addNewNote={addNewNote}/>
          <Right />
          </div>
  );
};

export default App;
