import React, { Component, useState } from "react";

const Tag = ({ seletedId, setNotes, notes }) => {
  const [tags, setTags] = useState([]);

  const addTags = (event) => {
    if (event.target.value != "") {
      setTags([...tags, event.target.value]);
      saveTags([...tags, event.target.value]);
      event.target.value = "";
    }
  };
  const deleteTags = (idxToDelete) => {
    setTags(tags.filter((_, idx) => idx != idxToDelete));
  };

  const saveTags = (newTags) => {
    setNotes(() => {
      return [
        ...notes.map((note, idx) =>
          idx != seletedId ? note : { ...note, tags: newTags }
        ),
      ];
    });
  };

  return (
    <>
      {seletedId >= 0 && (
        <div className="wholeTagBox" disabled={seletedId < 0}>
          <div className="wholeTagBox2">
            {tags.map((tag, idx) => (
              <div className="a-tag" key={idx}>
                <div className="a-tagText">{tag}</div>
                <button
                  className="material-icons closeTag"
                  onClick={() => deleteTags(idx)}
                >
                  close
                </button>
              </div>
            ))}
          </div>
          <input
            className="tag-input"
            placeholder="Enter a tag"
            value={notes[seletedId]?.tags || ""}
            onKeyUp={(e) => (e.key === "Enter" ? addTags(e) : null)}
          ></input>
        </div>
      )}
    </>
  );
};

export default Tag;
